r\n if($this->model_extension_soconfig_general->getUnitsSold($product_info[\'product_id\'])){ \r\n $sold = $this->model_extension_soconfig_general->getUnitsSold($product_info[\'product_id\']); \r\n } \r\n\r\n $data[\'orders\'] = sprintf($this->language->get(\'text_product_orders\'),$sold); \r\n /*====== insert image to the beginning of array=======*/ \r\n $firstimg = array(\'popup\' => $data[\'popup\'],\'thumb\' => $data[\'thumb\']); \r\n array_unshift($data[\'images\'], $firstimg); \r\n \r\n ]]></add> \r\n </operation> \r\n \r\n <operation> \r\n <search ><![CDATA[$data[\'products\'][] = array(]]></search> \r\n <add position=\"before\"><![CDATA[ \r\n if ((float)$result[\'special\']) $discount = \'-\'.round((($result[\'price\'] - $result[\'special\'])/$result[\'price\'])*100, 0).\'%\'; \r\n else $discount = false; \r\n ]]></add> \r\n </operation> \r\n <operation> \r\n <search><![CDATA[$data[\'products\'][] = array]]></search> \r\n <add position=\"after\" trim=\"true\" offset=\"2\"><![CDATA[ \r\n \'special_end_date\' => $this->model_extension_soconfig_general->getDateEnd($result[\'product_id\']), \r\n \'discount\' => $discount,]]></add> \r\n </operation> \r\n  \r\n </file> \r\n \r\n<!-- =======system/engine/router===============--> \r\n <file path=\"system/engine/router.php\"> \r\n <operation> \r\n <search ><![CDATA[while ($action instanceof Action)]]></search> \r\n <add position=\"before\"><![CDATA[ \r\n require_once(dirname(DIR_APPLICATION).\'/admin/view/template/extension/soconfig/class/soconfig.php\'); \r\n $soconfig = new Soconfig($this->registry); \r\n $this->registry->set(\'soconfig\', $soconfig); \r\n ]]></add> \r\n </operation> \r\n </file> \r\n \r\n<!-- ======= Show admin/controller==========--> \r\n <file path=\"admin/controller/design/layout.php\"> \r\n <operation > \r\n <search ><![CDATA[public function index()]]></search> \r\n <add position=\"before\" offset=\"3\"><![CDATA[require_once (DIR_APPLICATION.\'view/template/extension/soconfig/class/so_field.ph