(24, 32, 'So Color Swatch', 'So-color-swatch', 'OpenCartWorks', '1.0.1', 'http://www.opencartworks.com/', '<?xml version=\"1.0\" encoding=\"utf-8\"?>\r\n<modification>\r\n    <name>So Color Swatch</name>\r\n    <code>So-color-swatch</code>\r\n    <version>1.0.1</version>\r\n    <author>OpenCartWorks</author>\r\n    <link>http://www.opencartworks.com/</link>\r\n    \r\n    <file path=\"admin/controller/catalog/product.php\">\r\n        <operation>\r\n            <search><![CDATA[$data[\'layouts\'] = $this->model_design_layout->getLayouts();]]></search>\r\n            <add position=\"before\"><![CDATA[\r\n                $data[\'entry_default_image_color\'] = $this->language->get(\'entry_default_image_color\');\r\n                $data[\'entry_color\'] = $this->language->get(\'entry_color\');\r\n            ]]></add>\r\n        </operation>\r\n        <operation>\r\n            <search><![CDATA[$data[\'layouts\'] = $this->model_design_layout->getLayouts();]]></search>\r\n            <add position=\"after\"><![CDATA[\r\n                $this->load->model(\'extension/module/so_color_swatches_pro\');\r\n                $data[\'color_swatch\']   = $this->model_extension_module_so_color_swatches_pro->getColorSwatch();\r\n            ]]></add>\r\n        </operation>\r\n        <operation>\r\n			<search><![CDATA[$data[\'product_images\']]]></search>\r\n			<add position=\"before\"><![CDATA[\r\n				if(isset($product_image[\'default_of_color\'])){\r\n					$default_of_color = $product_image[\'default_of_color\'];\r\n				}else{\r\n					$default_of_color = array();\r\n				}           \r\n			]]></add>\r\n		</operation>\r\n		<operation>\r\n			<search><![CDATA[\'sort_order\' => $product_image[\'sort_order\']]]></search>\r\n			<add position=\"replace\"><![CDATA[\r\n				\'sort_order\' => $product_image[\'sort_order\'],\r\n				\'default_of_color\' => $default_of_color,                \r\n			]]></add>\r\n		</operation>\r\n    </file>\r\n    <file path=\"admin/view/template/catalog/product_form.twig\">\r\n        <operation>\r\n            <search><![CDATA[<td class=\"text-right\">{{ entry_sort_order }}</td>]]></search>\r\n            <add position=\"before\"><![CDATA[\r\n                <td class=\"text-right\">{{ entry_default_image_color }}</td>\r\n            ]]>\r\n            </add>\r\n        </operation>\r\n        <operation>\r\n            <search><![CDATA[<td class=\"text-right\"><input type=\"text\" name=\"product_image[{{ image_row }}][sort_order]\" value=\"{{ product_image.sort_order }}\" placeholder=\"{{ entry_sort_order }}\" class=\"form-control\"/></td>]]></search>\r\n            <add position=\"before\"><![CDATA[\r\n                <td class=\"text-right\">\r\n                    <select name=\"product_image[{{ image_row }}][default_of_color]\" class=\"default-color-selector\">\r\n                        <option value=\"0\">----------</option>\r\n                        {% for color in color_swatch %}\r\n                            {% if color.option_value_id == product_image.default_of_color %}\r\n                                <option value=\"{{ color.option_value_id }}\" selected=\"selected\">{{ color.name }}</option>\r\n                            {% else %}\r\n                                <option value=\"{{ color.option_value_id }}\">{{ color.name }}</option>\r\n                            {% endif %}\r\n                        {% endfor %}\r\n                    </select>\r\n                </td>\r\n            ]]>\r\n            </add>\r\n        </operation>\r\n        <operation>\r\n            <search><![CDATA[{{ footer }}]]></search>\r\n            <add position=\"before\"><![CDATA[\r\n                <script type=\"text/javascript\"><!--\r\n                    jQuery(document).ready(function($){\r\n                        $(\'.default-color-selector\').change(function(){\r\n                            var value = $(this).val();\r\n                            $(\'.default-color-selector\').not(this).each(function(){\r\n                                if($(this).val()==value)\r\n                                    $(this).val(\'0\');\r\n                            });\r\n                        })\r\n                    });\r\n                //--></script>\r\n            ]]></add>\r\n        </operation>\r\n        <operation>\r\n            <search index=\"2\"><![CDATA[<td colspan=\"2\"></td>]]></search>\r\n            <add position=\"replace\"><![CDATA[\r\n                <td colspan=\"3\"></td>\r\n            ]]></add>\r\n        </operation>\r\n        <operation>\r\n            <search><![CDATA[html += \'  <td class=\"text-right\"><input type=\"text\" name=\"product_image[\' + image_row + \'][sort_order]\" value=\"\" placeholder=\"{{ entry_sort_order }}\" class=\"form-control\" /></td>\';]]></search>\r\n            <add position=\"before\"><![CDATA[\r\n                html += \'  <td class=\"text-right\">\';\r\n                html += \'  <select name=\"product_image[\' + image_row + \'][default_of_color]\" class=\"default-color-selector\">\';\r\n                html += \'  <option value=\"0\">----------</option>\';\r\n                {% for color in color_swatch %}\r\n                    html += \'  <option value=\"{{ color.option_value_id }}>\">{{ color.name }}</option>\';\r\n                {% endfor %}\r\n                html += \'  </select>\';\r\n                html += \'  </td>\';\r\n            ]]></add>\r\n        </operation>\r\n    </file>\r\n    <file path=\"admin/model/catalog/product.php\">\r\n        <operation>\r\n            <search><![CDATA[$this->db->query(\"INSERT INTO \" . DB_PREFIX . \"product_image SET product_id = \'\" . (int)$product_id . \"\', image = \'\" . $this->db->escape($product_image[\'image\']) . \"\', sort_order = \'\" . (int)$product_image[\'sort_order\'] . \"\'\");]]></search>\r\n            <add position=\"replace\"><![CDATA[\r\n                if ($this->checkProductImageColumnExist()) {\r\n                    $this->db->query(\"INSERT INTO \" . DB_PREFIX . \"product_image SET product_id = \'\" . (int)$product_id . \"\', image = \'\" . $this->db->escape($product_image[\'image\']) . \"\', sort_order = \'\" . (int)$product_image[\'sort_order\'] . \"\', default_of_color = \'\". (int)$product_image[\'default_of_color\'] .\"\'\");\r\n                }\r\n                else {\r\n                    $this->db->query(\"INSERT INTO \" . DB_PREFIX . \"product_image SET product_id = \'\" . (int)$product_id . \"\', image = \'\" . $this->db->escape($product_image[\'image\']) . \"\', sort_order = \'\" . (int)$product_image[\'sort_order\'] . \"\'\");\r\n                }\r\n            ]]></add>\r\n        </operation>\r\n        <operation>\r\n            <search><![CDATA[public function getTotalProductsByTaxClassId($tax_class_id) {]]></search>\r\n            <add position=\"before\"><![CDATA[\r\n                public function checkProductImageColumnExist() {\r\n                    $sql = \"SHOW COLUMNS FROM \" .DB_PREFIX. \"product_image LIKE \'default_of_color\'\";\r\n                    $query = $this->db->query($sql);\r\n                    return count($query->rows)>0 ? true : false;\r\n                }\r\n            ]]></add>\r\n        </operation>\r\n    </file>\r\n    <file path=\"admin/language/*/catalog/product.php\">\r\n        <operation>\r\n             <search><![CDATA[// Entry]]></search>\r\n            <add position=\"after\"><![CDATA[\r\n                $_[\'entry_default_image_color\']        = \'Default Image For Color\';\r\n                $_[\'entry_color\']        = \'Color\';\r\n            ]]></add>\r\n        </operation>\r\n    </file>\r\n    <file path=\"catalog/controller/product/category.php\">\r\n        <operation>\r\n            <search><![CDATA[$data[\'products\'][] = array(]]></search>\r\n            <add position=\"before\"><![CDATA[\r\n                $option_data = array();\r\n                $image_data = array();\r\n                if ($this->config->get(\'module_so_color_swatches_pro_status\') && $this->config->get(\'module_so_color_swatches_pro_enable_product_list\')) {\r\n                    $this->load->model(\'extension/module/so_color_swatches_pro\');\r\n                    $data[\'width_product_list\'] = (int)$this->config->get(\'module_so_color_swatches_pro_width_product_list\');\r\n                    if ($data[\'width_product_list\'] == 0) {\r\n                        $data[\'width_product_list\'] = 15;\r\n                    }\r\n                    $data[\'height_product_list\'] = (int)$this->config->get(\'module_so_color_swatches_pro_height_product_list\');\r\n                    if ($data[\'height_product_list\'] == 0) {\r\n                        $data[\'height_product_list\'] = 15;\r\n                    }\r\n                    $data[\'colorswatch_type\'] = $this->config->get(\'module_so_color_swatches_pro_type\');\r\n                    $this->document->addStyle(\'catalog/view/javascript/so_color_swatches_pro/css/style.css\');\r\n\r\n                    $options = $this->model_extension_module_so_color_swatches_pro->getProductOptions($result[\'product_id\']);\r\n                    foreach ($options as $option) {\r\n                        $product_option_value_data = array();\r\n                        foreach ($option[\'product_option_value\'] as $option_value) {\r\n                            if (!$option_value[\'subtract\'] || ($option_value[\'quantity\'] > 0)) {\r\n                                $p_image = $this->model_extension_module_so_color_swatches_pro->getProductImages($result[\'product_id\'], $option_value[\'option_value_id\']);\r\n                                if (isset($p_image[\'image\']) && $p_image[\'image\']) {\r\n                                    $pimage = $this->model_tool_image->resize($p_image[\'image\'], $this->config->get(\'theme_\'.$this->config->get(\'config_theme\') . \'_image_product_width\'), $this->config->get(\'theme_\'.$this->config->get(\'config_theme\') . \'_image_pro